import Chart from './Chart'
function ExpenseChart(props){

    const data=[
        {label:'Jan',value:0},
        {label:'Feb',value:0},
        {label:'Mar',value:0},
        {label:'Apr',value:0},
        {label:'May',value:0},
        {label:'Jun',value:0},
        {label:'July',value:0},
        {label:'Aug',value:0},
        {label:'Sept',value:0},
        {label:'Oct',value:0},
        {label:'Nov',value:0},
        {label:'Dec',value:0},
    ]
    for(const exp of props.expenses){
            const month=exp.date.getMonth();
            data[month]+=exp.amount;
    };
    return <Chart items={data}/>
};

export default ExpenseChart;
